import React, { useState } from 'react';

const Ratings = () => {
  const [rating, setRating] = useState(0);

  const handleRatingChange = (event) => {
    // Ensure rating is within 1 to 5 range
    let newRating = parseInt(event.target.value);
    newRating = Math.max(1, Math.min(5, newRating));
    setRating(newRating);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Here you can handle the submission of the rating
    console.log('Submitted rating:', rating);
    // You can also send the rating to a server for processing
  };

  return (
    <div className="ratings-form-container">
      <h2>Rate This Book</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="rating">Ratings:</label>
          <input
            type="number"
            id="rating"
            name="rating"
            min="1"
            max="5"
            value={rating}
            onChange={handleRatingChange}
          />
        </div>
        <button type="submit">Submit Ratings</button>
      </form>
    </div>
  );
};

export default Ratings;
